import React, { useContext, useEffect, useState } from 'react';
import { Context } from '..'
import { getHeroes, getOneHero } from '../http/heroAPI'
import Slider from 'react-slick'
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import classes from "./css.modules/Documents.module.css";
import PrevArrowSlider from '../components/PrevArrowSlider'
import NextArrowSlider from '../components/NextArrowSlider'

const Documents = () => {
    const {hero} = useContext(Context);
    const [heroes, setHeroes] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    const settings = {
		autoplay: true,
		arrows: true,
		dots: false,
		infinite: true,
		speed: 2000,
        prevArrow: <PrevArrowSlider />,
		nextArrow: <NextArrowSlider />,
	}

    useEffect(() => {
        getHeroes().then(data => setHeroes(data)).then(() => setIsLoading(false));
        console.log(getOneHero(15));
    }, [])

    if (isLoading) {
        return <div>загрузка...</div>
    }

    return (
        <Slider
            {...settings}
            className={classes.slider}
        >
            {heroes.map((hero, index) =>
                <div className={classes.sliderItem} key={index}>
                    <h3>{hero.name}</h3>
                    <p>{hero.rank}</p>
                    <div>
                        awards
                    </div>
                </div>    
            )}
        </Slider>
    );
};

export default Documents;
